<SCRIPT LANGUAGE="JavaScript"> 
var endtime=new Date("2011/03/23,02:00:00");
var servertime= new Date("2011/06/17,13:51:50");
var clienttime = new Date();
function _fresh()
{
        
	  var nowtime=new Date();
      var leftsecond=parseInt((endtime.getTime()-(nowtime.getTime()+servertime.getTime()-clienttime.getTime()))/1000);
        __d=parseInt(leftsecond/3600/24);
        __h=parseInt((leftsecond/3600)%24);
        __m=parseInt((leftsecond/60)%60);
        __s=parseInt(leftsecond%60);
       	if(__h<=9){
		 __h="0"+__h	
		}
		
		if(__m<=9){
		 __m="0"+__m	
		}
		
		if(__s<=9){
		 __s="0"+__s	
		}
		
       
		if(__d<=1){
			document.getElementById("Counttime").innerHTML=__d+"<span>DAY</span> "+__h+":"+__m+":"+__s;
		}
		else{
			document.getElementById("Counttime").innerHTML=__d+"<span>DAYS</span> "+__h+":"+__m+":"+__s;
		}
		if(leftsecond<=0){
        document.getElementById("Counttime").innerHTML="";
        clearInterval(sh);
        }
}
_fresh()
var sh;
sh=setInterval(_fresh,1000);
</SCRIPT> 
